import React from 'react';
import { TextField, Grid, IconButton } from '@material-ui/core';
import InputAdorment from '@material-ui/core';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';

const input = ({ name, half, label, handleChange, type, handleShowPassword, autoFocus }) => {
    return (
        <Grid item xs={12} sm={half ? 6 : 12}>
            <TextField
                name={name}
                onChange={handleChange}
                variant="outlined"
                required
                fullWidth
                label={label}
                autoFocus={autoFocus}
                type={type}
                InputProps={name === 'password' && {
                    endAdorment: (
                        <InputAdorment position="end">
                            <IconButton onClick={handleShowPassword}>{type === "password" ? <Visibility /> : <VisibilityOff />}</IconButton>
                        </InputAdorment>
                    )
                }}
            ></TextField>
        </Grid>
    )
};

export default input;
